import React, { useState } from 'react';
import './RecommendationButton.css'; // Import CSS file for styling

const RecommendationButton = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // Toggle dropdown visibility
  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Render the dropdown menu items
  const renderDropdown = () => {
    if (isDropdownOpen) {
      return (
        <div className="dropdown">
          <ul>
            <li>Option 1</li>
            <li>Option 2</li>
            <li>Option 3</li>
            {/* Add more options as needed */}
          </ul>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="recommendationButtonContainer">
      <button className="recommendationButton" onClick={toggleDropdown}>
        {isDropdownOpen ? 'Hide Recommendations' : 'Show Recommendations'}
      </button>
      {renderDropdown()}
    </div>
  );
};

export default RecommendationButton;
