import React, { useState } from 'react';
import './UserPreferences.css'; // Import CSS file for styling
import PreferenceButton from './PreferenceButton';

const UserPreferences = () => {
  const [preferences, setPreferences] = useState([
    { id: 1, name: 'Sports', selected: false },
    { id: 2, name: 'Technology', selected: false },
    { id: 3, name: 'Music', selected: false },
    { id: 4, name: 'Food', selected: false },
  ]);

  const [selectedPreferences, setSelectedPreferences] = useState([]);

  const handlePreferenceClick = (id) => {
    const clickedPreference = preferences.find((preference) => preference.id === id);

    if (clickedPreference.selected) {
      // Remove the clicked preference from selected preferences
      const updatedSelectedPreferences = selectedPreferences.filter(
        (preference) => preference.id !== id
      );
      setSelectedPreferences(updatedSelectedPreferences);
    } else {
      // Add the clicked preference to selected preferences
      setSelectedPreferences([...selectedPreferences, clickedPreference]);
    }

    // Toggle the selected state of the clicked preference
    const updatedPreferences = preferences.map((preference) =>
      preference.id === id ? { ...preference, selected: !preference.selected } : preference
    );
    setPreferences(updatedPreferences);
  };

  return (
    <div className="userPreferences">
      <h2>Choose Your Preferences</h2>
      <div className="preferenceButtons">
        {preferences.map((preference) => (
          <PreferenceButton
            key={preference.id}
            preference={preference}
            onClick={handlePreferenceClick}
            isSelected={preference.selected}
          />
        ))}
      </div>
      <div className="selectedPreferences">
        {selectedPreferences.map((preference) => (
          <div className="selectedPreference" key={preference.id}>
            {preference.name}
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserPreferences;
