import React, { useState } from 'react';
import { FormControl, FormControlLabel, FormLabel, Radio, RadioGroup, Button, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import questionsData from './questionsData';
import './CareerGuidanceForm.css'; // Import CSS file

const useStyles = makeStyles((theme) => ({
  // Define styles using makeStyles if needed
}));

const CareerGuidanceForm = () => {
  const classes = useStyles();
  const [answers, setAnswers] = useState({});

  const handleAnswerChange = (event) => {
    const { name, value } = event.target;
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [name]: value === 'Yes', // Store boolean value for each question
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Form submitted with answers:', answers);
    // Add logic to handle form submission (e.g., send data to server)
  };

  return (
    <form className="formContainer" onSubmit={handleSubmit}>
      <Typography variant="h5" gutterBottom>Career Guidance Form</Typography>
      {questionsData.map((question, index) => (
        <div key={question.id} className="questionContainer">
          <Typography variant="body1" className="questionNumber">{index + 1}.</Typography>
          <FormControl component="fieldset">
            <FormLabel component="legend">{question.question}</FormLabel>
            <RadioGroup row aria-label={`question-${question.id}`} name={`question-${question.id}`} value={answers[`question-${question.id}`] ? 'Yes' : 'No'} onChange={handleAnswerChange}>
              <FormControlLabel value="Yes" control={<Radio color="primary" />} label="Yes" />
              <FormControlLabel value="No" control={<Radio color="primary" />} label="No" />
            </RadioGroup>
          </FormControl>
        </div>
      ))}
      <Button type="submit" variant="contained" color="primary">
        Submit
      </Button>
    </form>
  );
};

export default CareerGuidanceForm;
