import React from 'react';
import './PreferenceButton.css'; // Import CSS file for styling

const PreferenceButton = ({ preference, onClick, selectedPreferences }) => {
  const handleClick = () => {
    onClick(preference.id); // Pass the preference ID to the click handler
  };

  const isSelected = selectedPreferences.includes(preference.id);

  return (
    <button
      className={`preferenceButton ${isSelected ? 'selected' : ''}`}
      onClick={handleClick}
    >
      {preference.name}
    </button>
  );
};

export default PreferenceButton;
